import axios from "axios";
import React, { useState } from "react";
import { URL_POST_USER } from "../assets/constants";

const SignIn = () => {
  const [formData, setFormData] = useState({
    username: "",
    password: "",
  });

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    console.log("Form submitted with data:", formData);
    try {
      const response = await axios.post(URL_POST_USER, formData);

      if (response.status === 201) {
        console.log("User successfully created!");
        // You can perform further actions here, such as showing a success message to the user.
      } else {
        console.log("User creation failed:", response.status);
        // Handle other status codes or error scenarios here.
      }
    } catch (error) {
      console.error("An error occurred:", error);
      // Handle errors that occur during the POST request.
    }
  };

  // console.log("newRecipe en postRecipe", isNewUserCreated);
  // You can perform further actions like sending data to a server here

  return (
    <div className="container d-flex justify-content-center align-items-center vh-100">
      <h3>Create a new username</h3>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label htmlFor="username" className="form-label">
            Username:
          </label>
          <input
            type="text"
            id="username"
            name="username"
            value={formData.username}
            onChange={handleInputChange}
            className="form-control"
          />
        </div>
        <div className="mb-3">
          <label htmlFor="password" className="form-label">
            Password:
          </label>
          <input
            type="password"
            id="password"
            name="password"
            value={formData.password}
            onChange={handleInputChange}
            className="form-control"
          />
        </div>
        <button className="btn btn-primary mb-3" type="submit">
          Log in
        </button>
      </form>
    </div>
  );
};

export default SignIn;
